const Table = require('cli-table3');
const chalk = require('chalk');

/**
 * Format data as table
 */
function formatTable(data) {
  const table = new Table({
    head: [chalk.cyan('Property'), chalk.cyan('Value')],
    colWidths: [34, 60]
  });

  // Recursively flatten object to key paths
  function flatten(obj, prefix = '') {
    const rows = [];
    Object.entries(obj).forEach(([key, value]) => {
      const fullKey = prefix ? `${prefix}.${key}` : key;
      if (value instanceof Date) {
        rows.push([fullKey, value.toISOString()]);
      } else if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
        rows.push([fullKey, chalk.gray('(see details)')]);
        rows.push(...flatten(value, fullKey));
      } else {
        rows.push([fullKey, typeof value === 'number' ? value.toFixed(3) : String(value)]);
      }
    });
    return rows;
  }

  const rows = flatten(data);
  rows.forEach(([k, v]) => table.push([k, v]));

  return table.toString();
}

/**
 * Format data as JSON
 */
function formatJSON(data) {
  return JSON.stringify(data, null, 2);
}

/**
 * Format data as CSV
 */
function formatCSV(data) {
  const rows = [];

  function flatten(obj, prefix = '') {
    Object.entries(obj).forEach(([key, value]) => {
      const fullKey = prefix ? `${prefix}.${key}` : key;
      if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
        flatten(value, fullKey);
      } else {
        rows.push([fullKey, value]);
      }
    });
  }

  flatten(data);

  // Add header
  const output = ['Property,Value'];
  rows.forEach(([key, value]) => {
    output.push(`${key},${value}`);
  });

  return output.join('\n');
}

/**
 * Format based on type
 */
function format(data, type = 'table') {
  switch (type.toLowerCase()) {
    case 'json':
      return formatJSON(data);
    case 'csv':
      return formatCSV(data);
    case 'table':
    default:
      return formatTable(data);
  }
}

module.exports = {
  format,
  formatTable,
  formatJSON,
  formatCSV
};

const fs = require('fs');
const path = require('path');

const TOKEN_FILE = path.join(process.cwd(), '.antikythera', 'control-token');

function getControlToken() {
  const envToken = process.env.ANTIKYTHERA_CONTROL_TOKEN || process.env.CONTROL_TOKEN;
  if (envToken && String(envToken).trim().length > 0) return String(envToken).trim();
  try {
    if (fs.existsSync(TOKEN_FILE)) {
      const v = fs.readFileSync(TOKEN_FILE, 'utf8').trim();
      if (v) return v;
    }
  } catch (_) {}
  return null;
}

function requireControlToken() {
  const token = getControlToken();
  if (!token) {
    console.error('✗ Control token not found');
    console.error('  Start the server first: npm start');
    console.error('  Or set: export ANTIKYTHERA_CONTROL_TOKEN=your-token');
    process.exit(1);
  }
  return token;
}

module.exports = { getControlToken, requireControlToken, TOKEN_FILE };

const { z } = require('zod');

// Celestial body position schema
const PositionSchema = z.object({
  longitude: z.number(),
  latitude: z.number(),
  rightAscension: z.number(),
  declination: z.number()
});

// Sun position schema (has additional altitude/azimuth)
const SunSchema = PositionSchema.extend({
  altitude: z.number(),
  azimuth: z.number()
});

// Moon schema (has phase information)
const MoonSchema = PositionSchema.extend({
  phase: z.number(),
  illumination: z.number(),
  age: z.number()
});

// Planets schema
const PlanetsSchema = z.object({
  mercury: PositionSchema,
  venus: PositionSchema,
  mars: PositionSchema,
  jupiter: PositionSchema,
  saturn: PositionSchema
});

// Full API response schema
const APIResponseSchema = z.object({
  date: z.string(),
  location: z.object({
    latitude: z.number(),
    longitude: z.number()
  }),
  sun: SunSchema,
  moon: MoonSchema,
  planets: PlanetsSchema,
  zodiac: z.object({
    sign: z.string(),
    signIndex: z.number(),
    degreeInSign: z.number(),
    absoluteLongitude: z.number()
  }),
  egyptianCalendar: z.object({
    month: z.number(),
    day: z.number(),
    dayOfYear: z.number()
  }),
  metonicCycle: z.object({
    year: z.number(),
    progress: z.number(),
    anglePosition: z.number()
  }),
  sarosCycle: z.object({
    cycle: z.number(),
    progress: z.number(),
    anglePosition: z.number(),
    daysUntilNext: z.number()
  })
}).passthrough(); // Allow extra fields

module.exports = {
  APIResponseSchema,
  PositionSchema,
  SunSchema,
  MoonSchema,
  PlanetsSchema
};

const AntikytheraEngine = require('../../engine');
const axios = require('axios');
const { APIResponseSchema } = require('../schemas');
const chalk = require('chalk');

const engine = new AntikytheraEngine();
let fallbackNotified = false;
let lastApiFailureAt = 0;
const API_COOLDOWN_MS = 60 * 1000; // 60s cooldown after failure
function notifyFallbackOnce() {
  if (fallbackNotified) return;
  try {
    // Keep this plain for easy grepping in demos/tests
    console.log('API timeout... Falling back to local engine');
  } catch (_) {}
  fallbackNotified = true;
}
function apiAvailable() {
  return (Date.now() - lastApiFailureAt) >= API_COOLDOWN_MS;
}

/**
 * Get astronomical data from embedded engine
 */
async function getFromEngine(date = new Date(), observer = null) {
  if (observer && isFinite(observer.latitude) && isFinite(observer.longitude)) {
    return engine.getState(date, observer.latitude, observer.longitude, observer);
  }
  return engine.getState(date);
}

/**
 * Get astronomical data from API server
 */
async function getFromAPI(date = new Date(), { validate = true, observer = null } = {}) {
  try {
    const params = new URLSearchParams({ date: date.toISOString() });
    if (observer && isFinite(observer.latitude) && isFinite(observer.longitude)) {
      params.set('lat', String(observer.latitude));
      params.set('lon', String(observer.longitude));
      if (isFinite(observer.elevation)) params.set('elev', String(observer.elevation));
    }
    const url = `http://localhost:3000/api/state?${params.toString()}`;
    const response = await axios.get(url, { timeout: 5000 });

    // Validate API response
    if (validate) {
      try {
        APIResponseSchema.parse(response.data);
      } catch (validationError) {
        console.warn(chalk.yellow('⚠ API response validation failed:'));
        validationError.errors.forEach(err => {
          console.warn(chalk.gray(`  ${err.path.join('.')}: ${err.message}`));
        });
        // Return data anyway but warn user
      }
    }

    return response.data;
  } catch (error) {
    lastApiFailureAt = Date.now();
    if (error.code === 'ECONNREFUSED') {
      throw new Error('API server not running. Start with: npm start');
    }
    throw error;
  }
}

/**
 * Get astronomical data - smart selection between engine and API
 */
async function getData(date = new Date(), options = {}) {
  const { local, remote, observer = null } = options;

  // Force local
  if (local) {
    return getFromEngine(date, observer);
  }

  // Force remote
  if (remote) {
    return getFromAPI(date, { observer });
  }

  // Smart: try API first unless in cooldown, fallback to engine
  if (!apiAvailable()) {
    notifyFallbackOnce();
    return getFromEngine(date, observer);
  }
  try {
    return await getFromAPI(date, { observer });
  } catch (_error) {
    // Notify once for demo/UX visibility
    notifyFallbackOnce();
    return getFromEngine(date, observer);
  }
}

async function getDisplayFromAPI(date = new Date(), observer = null) {
  try {
    const params = new URLSearchParams({ date: date.toISOString() });
    if (observer && isFinite(observer.latitude) && isFinite(observer.longitude)) {
      params.set('lat', String(observer.latitude));
      params.set('lon', String(observer.longitude));
      if (isFinite(observer.elevation)) params.set('elev', String(observer.elevation));
    }
    const url = `http://localhost:3000/api/display?${params.toString()}`;
    const response = await axios.get(url, { timeout: 5000 });
    return response.data;
  } catch (error) {
    lastApiFailureAt = Date.now();
    if (error.code === 'ECONNREFUSED') {
      throw new Error('API server not running. Start with: npm start');
    }
    throw error;
  }
}

async function getNextConjunction(date = new Date(), a = 'moon', b = 'sun') {
  const A = String(a || 'moon');
  const B = String(b || 'sun');
  return engine.getNextConjunction(date, A, B);
}

async function getNextEquinox(date = new Date()) {
  return engine.getNextEquinox(date);
}
async function getNextSolstice(date = new Date()) {
  return engine.getNextSolstice(date);
}

module.exports = {
  getData,
  getFromEngine,
  getFromAPI,
  getDisplayFromAPI,
  getNextConjunction,
  getNextEquinox,
  getNextSolstice
};

function parseRelative(input, base) {
  const m = input.match(/^([+-])(\d+)([smhdwy])$/i);
  if (!m) return null;
  const sign = m[1] === '+' ? 1 : -1;
  const n = parseInt(m[2], 10);
  const unit = m[3].toLowerCase();
  const msPer = { s: 1e3, m: 6e4, h: 3.6e6, d: 8.64e7, w: 6.048e8, y: 3.15576e10 };
  const delta = (msPer[unit] || 0) * n * sign;
  if (!delta) return null;
  // Guard: relative offsets larger than ±100 years are rejected
  const LIMIT_MS = 100 * 365.25 * 24 * 3600 * 1000;
  if (Math.abs(delta) > LIMIT_MS) throw new Error('Relative offset too large (±100y max)');
  return new Date(base.getTime() + delta);
}

function parseNatural(input, base) {
  const low = input.toLowerCase().trim();
  const now = base || new Date();
  // today HH:mm
  let m = low.match(/^today\s+(\d{1,2}):(\d{2})$/);
  if (m) {
    const h = parseInt(m[1], 10), min = parseInt(m[2], 10);
    const d = new Date(now);
    d.setHours(h, min, 0, 0);
    return d;
  }
  // tomorrow [HH:mm|noon]
  m = low.match(/^tomorrow(?:\s+(\d{1,2}):(\d{2})|\s+noon)?$/);
  if (m) {
    const d = new Date(now);
    d.setDate(d.getDate() + 1);
    if (m[1]) d.setHours(parseInt(m[1], 10), parseInt(m[2], 10), 0, 0);
    else if (low.includes('noon')) d.setHours(12, 0, 0, 0);
    else d.setHours(0, 0, 0, 0);
    return d;
  }
  // tonight HH:mm → today at time (if past, keep today)
  m = low.match(/^tonight\s+(\d{1,2}):(\d{2})$/);
  if (m) {
    const d = new Date(now);
    d.setHours(parseInt(m[1], 10), parseInt(m[2], 10), 0, 0);
    return d;
  }
  return null;
}

function isISO(str) {
  // Quick ISO check
  return /\d{4}-\d{2}-\d{2}/.test(str);
}

function parseDateInput(input, context) {
  const base = new Date();

  // ISO first (let Date parse with timezone/UTC if present)
  if (isISO(input)) {
    const d = new Date(input);
    if (!isNaN(d.getTime())) {
      return { date: d, echo: echoParsedDate(d, context) };
    }
  }

  // Relative
  const rel = parseRelative(input, base);
  if (rel) return { date: rel, echo: echoParsedDate(rel, context, input) };

  // Natural minimal
  const nat = parseNatural(input, base);
  if (nat) return { date: nat, echo: echoParsedDate(nat, context, input) };

  // Invalid
  throw new Error('Invalid date. Use ISO (YYYY-MM-DDTHH:mm:ssZ) or relative (+2h, -30m).');
}

function echoParsedDate(d, context) {
  try {
    // We only echo UTC here; local side is the system tz or context tz label
    const tzLabel = (context && context.tz && context.tz !== 'auto') ? context.tz : Intl.DateTimeFormat().resolvedOptions().timeZone;
    const local = d.toLocaleString('en-US', { timeZone: tzLabel, hour12: false }).replace(',', '');
    const utc = d.toISOString();
    return `Parsed: ${local} ${tzLabel} → ${utc}`;
  } catch (_) {
    return d.toISOString();
  }
}

// Apply a relative offset like +2h/-30m to a base date
function addRelativeToDate(input, baseDate) {
  const base = baseDate instanceof Date ? baseDate : new Date(baseDate);
  const d = parseRelative(String(input), base);
  if (!d) throw new Error('Invalid relative offset. Use +2h, -30m, +7d, etc.');
  return d;
}

module.exports = { parseDateInput, echoParsedDate, addRelativeToDate };

