const astronomy = require('astronomy-engine');
const { MS_PER_DAY } = require('./constants/time');
const TimeUtils = require('./utils/time');
const { getUtcOffsetMinutes } = require('./utils/tz');

class AntikytheraEngine {
  /**
   * Convert an equator-of-date vector to J2000 mean ecliptic angles
   */
  eclipticFromEquatorVec_EQD(date, equatorVec) {
    const rot = astronomy.Rotation_EQD_ECL(date);
    const eclVec = astronomy.RotateVector(rot, equatorVec);
    const sph = astronomy.SphereFromVector(eclVec);
    let elon = sph.lon % 360; if (elon < 0) elon += 360;
    return { elon, elat: sph.lat };
  }

  eclipticFromEquatorVec_EQJ(equatorJ2000Vec) {
    // Convert J2000 equator topocentric vector to true ecliptic of date
    const ecl = astronomy.Ecliptic(equatorJ2000Vec);
    // astronomy.Ecliptic returns EclipticCoordinates with elon/elat
    return { elon: ecl.elon, elat: ecl.elat };
  }

  /**
   * Calculate sun visibility and daylight information for a given location and time
   */
  getSunVisibility(date, observer) {
    // Current sun position
    const sunPos = this.getSunPosition(date, observer);

    // Find sunrise and sunset for this day
    // Use a search window that ensures we get today's sunrise/sunset
    const startOfDay = TimeUtils.utcStartOfDay(date);
    const _endOfDay = TimeUtils.utcEndOfDay(date);

    let sunrise = null;
    let sunset = null;

    try {
      // Search for sunrise (sun rising above horizon) within today
      sunrise = astronomy.SearchRiseSet('Sun', observer, 1, startOfDay, 1);
      // Verify it's actually today (using UTC date comparison)
      if (sunrise) {
        const sunriseDate = sunrise.date || sunrise;
        if (!TimeUtils.sameUtcDate(sunriseDate, date)) {
          sunrise = null;
        }
      }

      // Search for sunset (sun setting below horizon) within today
      sunset = astronomy.SearchRiseSet('Sun', observer, -1, startOfDay, 1);
      // Verify it's actually today (using UTC date comparison)
      if (sunset) {
        const sunsetDate = sunset.date || sunset;
        if (!TimeUtils.sameUtcDate(sunsetDate, date)) {
          sunset = null;
        }
      }
    } catch (_e) {
      // Handle polar day/night (no sunrise or sunset)
      const noon = TimeUtils.utcNoon(date);
      const noonSun = this.getSunPosition(noon, observer);

      if (noonSun.altitude > 0) {
        // Polar day - sun never sets
        return {
          currentPosition: {
            azimuth: sunPos.azimuth,
            altitude: sunPos.altitude,
            isVisible: true
          },
          sunrise: null,
          sunset: null,
          daylight: {
            hours: 24,
            percent: 1.0,
            arcDegrees: 360,
            type: 'polar_day'
          }
        };
      } else {
        // Polar night - sun never rises
        return {
          currentPosition: {
            azimuth: sunPos.azimuth,
            altitude: sunPos.altitude,
            isVisible: false
          },
          sunrise: null,
          sunset: null,
          daylight: {
            hours: 0,
            percent: 0.0,
            arcDegrees: 0,
            type: 'polar_night'
          }
        };
      }
    }

    // Get sun positions at sunrise and sunset
    const sunriseDate = sunrise ? (sunrise.date || sunrise) : null;
    const sunsetDate = sunset ? (sunset.date || sunset) : null;
    const sunrisePos = sunriseDate ? this.getSunPosition(sunriseDate, observer) : null;
    const sunsetPos = sunsetDate ? this.getSunPosition(sunsetDate, observer) : null;

    // Calculate daylight duration
    const daylightMs = sunsetDate - sunriseDate;
    const daylightHours = daylightMs / (1000 * 60 * 60);
    const daylightPercent = daylightHours / 24;
    const arcDegrees = daylightPercent * 360;

    return {
      currentPosition: {
        azimuth: sunPos.azimuth,
        altitude: sunPos.altitude,
        isVisible: sunPos.altitude > 0
      },
      sunrise: sunrise ? {
        time: (sunrise.date || sunrise),
        azimuth: sunrisePos.azimuth
      } : null,
      sunset: sunset ? {
        time: (sunset.date || sunset),
        azimuth: sunsetPos.azimuth
      } : null,
      daylight: {
        hours: daylightHours,
        percent: daylightPercent,
        arcDegrees: arcDegrees,
        type: 'normal'
      }
    };
  }

  /**
   * Calculate Equation of Time - difference between apparent and mean solar time
   */
  getEquationOfTime(date) {
    // Calculate fractional day of year using centralized time utilities
    const dayOfYear = TimeUtils.fractionalDayOfYear(date);

    // B parameter (in radians)
    const B = (2 * Math.PI / 365.25) * (dayOfYear - 81);

    // Equation of Time in minutes (simplified formula)
    const EoT = 9.87 * Math.sin(2 * B) - 7.53 * Math.cos(B) - 1.5 * Math.sin(B);

    // Mean sun longitude (uniform motion: 360° / 365.25 days)
    // This represents where the sun WOULD be if it moved uniformly
    const meanLongitude = ((dayOfYear - 1) * (360 / 365.25)) % 360;

    // Get actual sun position for apparent longitude
    const observer = new astronomy.Observer(37.5, 23.0, 0);
    const sunPos = this.getSunPosition(date, observer);
    const apparentLongitude = sunPos.longitude;

    return {
      equationOfTime: {
        minutes: EoT,
        degrees: EoT / 4,
        status: EoT > 0 ? 'ahead' : 'behind'
      },
      meanSun: {
        longitude: meanLongitude,
        degreeOfYear: meanLongitude
      },
      apparentSun: {
        longitude: apparentLongitude,
        degreeOfYear: apparentLongitude
      }
    };
  }

  /**
   * Get the complete state of the Antikythera mechanism for a given date
   */
  getState(date = new Date(), latitude = 37.5, longitude = 23.0, observerInfo = null) {
    const elevation = observerInfo && typeof observerInfo.elevation === 'number' ? observerInfo.elevation : 0;
    const observer = new astronomy.Observer(latitude, longitude, elevation);

    const observerOut = observerInfo ? { ...observerInfo } : { latitude, longitude };
    if (observerOut && observerOut.timezone) {
      const off = getUtcOffsetMinutes(date, observerOut.timezone);
      if (off !== null) observerOut.utcOffsetMinutes = off;
    }

    return {
      date: date.toISOString(),
      location: { latitude, longitude },
      observer: observerOut,
      sun: this.getSunPosition(date, observer),
      moon: this.getMoonPosition(date, observer),
      planets: this.getPlanetaryPositions(date, observer),
      zodiac: this.getZodiacPosition(date),
      egyptianCalendar: this.getEgyptianCalendar(date),
      metonicCycle: this.getMetonicCycle(date),
      sarosCycle: this.getSarosCycle(date),
      lunarNodes: this.getLunarNodes(date),
      nextEclipse: this.getNextEclipse(date, observer),
      nextOpposition: this.getNextOpposition(date),
      equationOfTime: this.getEquationOfTime(date),
      sunVisibility: this.getSunVisibility(date, observer)
    };
  }

  getSunPosition(date, observer) {
    const equator = astronomy.Equator('Sun', date, observer, false, true); // EQJ
    const horizonEqd = astronomy.Equator('Sun', date, observer, true, true);
    const horizon = astronomy.Horizon(date, observer, horizonEqd.ra, horizonEqd.dec, 'normal');
    const ecliptic = this.eclipticFromEquatorVec_EQJ(equator.vec);

    const velocity = this.getVelocity('Sun', date, observer);

    return {
      longitude: ecliptic.elon, // Ecliptic longitude (degrees)
      latitude: ecliptic.elat,
      rightAscension: equator.ra,
      declination: equator.dec,
      altitude: horizon.altitude,
      azimuth: horizon.azimuth,
      velocity: velocity, // degrees per day
      angularVelocity: velocity / 24 // degrees per hour
    };
  }

  getMoonPosition(date, observer) {
    const phase = astronomy.MoonPhase(date);
    const equator = astronomy.Equator('Moon', date, observer, false, true); // EQJ
    const ecliptic = this.eclipticFromEquatorVec_EQJ(equator.vec);
    const illumination = astronomy.Illumination('Moon', date);
    const horizon = astronomy.Horizon(date, observer, equator.ra, equator.dec, 'normal');

    const velocity = this.getVelocity('Moon', date, observer);

    return {
      longitude: ecliptic.elon,
      latitude: ecliptic.elat,
      phase: phase, // 0-360 degrees
      illumination: illumination.phase_fraction,
      age: phase / 12.368, // Approximate age in days (360°/29.53 days per degree)
      rightAscension: equator.ra,
      declination: equator.dec,
      altitude: horizon.altitude,
      azimuth: horizon.azimuth,
      velocity: velocity, // degrees per day
      angularVelocity: velocity / 24 // degrees per hour
    };
  }

  /**
   * Calculate velocity (degrees per day) for a celestial body
   * Uses 1-day delta to determine rate of change
   */
  getVelocity(body, date, observer) {
    const currentEquator = astronomy.Equator(body, date, observer, false, true);
    const currentEcliptic = this.eclipticFromEquatorVec_EQJ(currentEquator.vec);
    const currentLongitude = currentEcliptic.elon;

    // Calculate position 1 day later
    const nextDate = new Date(date.getTime() + MS_PER_DAY);
    const nextEquator = astronomy.Equator(body, nextDate, observer, false, true);
    const nextEcliptic = this.eclipticFromEquatorVec_EQJ(nextEquator.vec);
    const nextLongitude = nextEcliptic.elon;

    // Handle 360° wraparound (e.g., 359° -> 1°)
    let delta = nextLongitude - currentLongitude;
    if (delta > 180) {
      delta -= 360;
    } else if (delta < -180) {
      delta += 360;
    }

    return delta; // degrees per day
  }

  getPlanetaryPositions(date, observer) {
    const planets = ['Mercury', 'Venus', 'Mars', 'Jupiter', 'Saturn'];
    const positions = {};

    planets.forEach(planet => {
      const equator = astronomy.Equator(planet, date, observer, false, true);
      const ecliptic = this.eclipticFromEquatorVec_EQJ(equator.vec);
      const horizon = astronomy.Horizon(date, observer, equator.ra, equator.dec, 'normal');

      // Calculate velocity and retrograde status
      const velocity = this.getVelocity(planet, date, observer);
      const isRetrograde = velocity < 0;

      positions[planet.toLowerCase()] = {
        longitude: ecliptic.elon,
        latitude: ecliptic.elat,
        rightAscension: equator.ra,
        declination: equator.dec,
        altitude: horizon.altitude,
        azimuth: horizon.azimuth,
        velocity: velocity, // degrees per day
        angularVelocity: velocity / 24, // degrees per hour
        isRetrograde: isRetrograde,
        motionState: isRetrograde ? 'retrograde' : 'prograde'
      };
    });

    return positions;
  }

  getZodiacPosition(date) {
    // Get Sun's ecliptic longitude (zodiac is based on Sun, not Moon!)
    const observer = new astronomy.Observer(0, 0, 0); // Ecliptic position same from anywhere
    const sunEquator = astronomy.Equator('Sun', date, observer, false, true); // EQJ
    const sunEcliptic = this.eclipticFromEquatorVec_EQJ(sunEquator.vec);
    const longitude = sunEcliptic.elon;

    // Zodiac signs, 30 degrees each
    const signs = [
      'Aries', 'Taurus', 'Gemini', 'Cancer', 'Leo', 'Virgo',
      'Libra', 'Scorpio', 'Sagittarius', 'Capricorn', 'Aquarius', 'Pisces'
    ];

    const signIndex = Math.floor(longitude / 30);
    const degreeInSign = longitude % 30;

    return {
      sign: signs[signIndex],
      signIndex: signIndex,
      degreeInSign: degreeInSign,
      absoluteLongitude: longitude
    };
  }

  getEgyptianCalendar(date) {
    // Modern display: drive the calendar pointer by accurate year progress,
    // but show Gregorian month/day in the label.
    const yearProgress = TimeUtils.yearProgress360(date); // 0..360
    const month = date.getUTCMonth() + 1; // 1-12 (Gregorian, UTC)
    const day = date.getUTCDate();        // 1-31 (Gregorian, UTC)

    return {
      month,
      day,
      dayOfYear: yearProgress // used for ring geometry (outer pointer)
    };
  }

  getMetonicCycle(date) {
    // Metonic cycle: 19 solar years = 235 lunar months
    // Reference: 2000-01-01 as year 0 of cycle
    const referenceDate = new Date('2000-01-01');
    const yearsSince = (date - referenceDate) / (1000 * 60 * 60 * 24 * 365.25);

    const metonicYear = Math.floor(yearsSince % 19) + 1;
    const progress = (yearsSince % 19) / 19; // 0-1 for dial position

    return {
      year: metonicYear,
      progress: progress,
      anglePosition: progress * 360 // Degrees on spiral dial
    };
  }

  getSarosCycle(date) {
    // Saros cycle: 223 synodic months ≈ 18 years, 11 days, 8 hours
    // Used for eclipse predictions
    const sarosLength = 6585.32; // days
    const referenceDate = new Date('2000-01-06'); // Known eclipse date

    const daysSince = (date - referenceDate) / (1000 * 60 * 60 * 24);
    const cycleNumber = Math.floor(daysSince / sarosLength);
    const progress = (daysSince % sarosLength) / sarosLength;

    return {
      cycle: cycleNumber,
      progress: progress,
      anglePosition: progress * 360,
      daysUntilNext: sarosLength * (1 - progress)
    };
  }

  getNextEclipse(date, observer) {
    try {
      const lunarEclipse = astronomy.SearchLunarEclipse(date);
      const solarEclipse = astronomy.SearchGlobalSolarEclipse(date);

      const next = (lunarEclipse.peak.date < solarEclipse.peak.date)
        ? { type: 'lunar', data: lunarEclipse }
        : { type: 'solar', data: solarEclipse };

      const peakDate = next.data.peak && (next.data.peak.date || (next.data.peak.time && next.data.peak.time.date))
        ? (next.data.peak.date || next.data.peak.time.date)
        : null;

      const base = {
        type: next.type,
        kind: next.data.kind || null,
        date: peakDate ? peakDate.toISOString() : null,
        daysUntil: peakDate ? ((peakDate - date) / (86400000)) : null
      };

      // Add local observer context when solar eclipse is next
      if (next.type === 'solar' && observer) {
        try {
          const local = astronomy.SearchLocalSolarEclipse(date, observer);
          const toIso = (ev) => {
            if (!ev) return null;
            const t = ev.date || (ev.time && ev.time.date) || ev.time || ev;
            return t instanceof Date ? t.toISOString() : null;
          };
          base.local = {
            kind: local.kind || null,
            obscuration: typeof local.obscuration === 'number' ? local.obscuration : null,
            partialBegin: toIso(local.partialBegin),
            totalBegin: toIso(local.totalBegin),
            peak: toIso(local.peak),
            totalEnd: toIso(local.totalEnd),
            partialEnd: toIso(local.partialEnd)
          };
        } catch (_e) {
          // ignore local failure
        }
      }

      // Add extra lunar details if available
      if (next.type === 'lunar') {
        base.details = {
          sdPenum: next.data.sdPenum || null,
          sdPartial: next.data.sdPartial || null,
          sdTotal: next.data.sdTotal || null,
          obscuration: typeof next.data.obscuration === 'number' ? next.data.obscuration : null
        };
      }

      return base;
    } catch (_err) {
      return { error: 'Could not calculate next eclipse' };
    }
  }

  /**
   * Find the next planetary opposition (frame-stable)
   * Opposition occurs when the planet's geocentric ecliptic longitude is 180° from the Sun's.
   * Uses Astronomy.PairLongitude and a bracket + bisection search for accuracy.
   */
  getNextOpposition(date, maxDays = 1095) {
    const planets = ['Mars', 'Jupiter', 'Saturn'];
    const synodic = { Mars: 780, Jupiter: 399, Saturn: 378 };

    // Normalize angle to [0,360)
    const norm = (a) => ((a % 360) + 360) % 360;
    // Signed smallest difference a-b in [-180,180]
    const diff = (a, b) => {
      let d = norm(a) - norm(b);
      if (d > 180) d -= 360;
      if (d < -180) d += 360;
      return d;
    };

    const rel = (planet, t) => astronomy.PairLongitude(planet, 'Sun', t);

    const findOpposition = (planet, start) => {
      const limit = synodic[planet] || 800;
      const end = new Date(start.getTime() + limit * MS_PER_DAY);
      // Coarse daily scan to find sign change around target 180°
      let t0 = new Date(start);
      let f0 = diff(rel(planet, t0), 180);
      for (let t = new Date(t0.getTime() + MS_PER_DAY); t <= end; t = new Date(t.getTime() + MS_PER_DAY)) {
        const f1 = diff(rel(planet, t), 180);
        if (f0 === 0) return t0;
        if (f0 * f1 <= 0) {
          // Bracket found between t0 and t
          let lo = t0, hi = t;
          for (let i = 0; i < 30; i++) {
            const mid = new Date((lo.getTime() + hi.getTime()) / 2);
            const fm = diff(rel(planet, mid), 180);
            if (f0 * fm <= 0) {
              hi = mid;
            } else {
              lo = mid;
              f0 = fm;
            }
            if ((hi.getTime() - lo.getTime()) < 1000) break; // ~1s
          }
          return new Date((lo.getTime() + hi.getTime()) / 2);
        }
        t0 = t; f0 = f1;
      }
      return null;
    };

    const results = [];
    for (const planet of planets) {
      try {
        const when = findOpposition(planet, date);
        if (when) {
          results.push({
            planet,
            date: when.toISOString(),
            daysUntil: (when - date) / 86400000
          });
        }
      } catch (_err) {
        // skip
      }
    }

    if (!results.length) return { error: 'No oppositions found', searchedDays: maxDays };
    results.sort((a, b) => a.daysUntil - b.daysUntil);
    return results[0];
  }

  /**
   * Find the next conjunction between two bodies (geocentric ecliptic longitude equality)
   */
  getNextConjunction(date, bodyA = 'Moon', bodyB = 'Sun', maxDays = 1095) {
    const cap = (s) => String(s || '').toLowerCase().replace(/^(\w)/, (m, c) => c.toUpperCase());
    const A = cap(bodyA);
    const B = cap(bodyB);

    const norm = (a) => ((a % 360) + 360) % 360;
    const diff = (a, b) => {
      let d = norm(a) - norm(b);
      if (d > 180) d -= 360;
      if (d < -180) d += 360;
      return d;
    };

    const rel = (t) => astronomy.PairLongitude(A, B, t);

    const end = new Date(date.getTime() + maxDays * 86400000);
    let t0 = new Date(date);
    let f0 = diff(rel(t0), 0);
    for (let t = new Date(t0.getTime() + 86400000); t <= end; t = new Date(t.getTime() + 86400000)) {
      const f1 = diff(rel(t), 0);
      if (f0 === 0) return { bodies: [A, B], date: t0.toISOString(), daysUntil: (t0 - date) / 86400000 };
      if (f0 * f1 <= 0) {
        let lo = t0, hi = t;
        for (let i = 0; i < 30; i++) {
          const mid = new Date((lo.getTime() + hi.getTime()) / 2);
          const fm = diff(rel(mid), 0);
          if (f0 * fm <= 0) hi = mid; else { lo = mid; f0 = fm; }
          if ((hi.getTime() - lo.getTime()) < 1000) break;
        }
        const when = new Date((lo.getTime() + hi.getTime()) / 2);
        return { bodies: [A, B], date: when.toISOString(), daysUntil: (when - date) / 86400000 };
      }
      t0 = t; f0 = f1;
    }
    return { error: `No conjunction found for ${A}-${B} within ${maxDays} days` };
  }

  /**
   * Next equinox after given date (March/Sep) using Astronomy.Seasons.
   */
  getNextEquinox(date) {
    try {
      const y = date.getUTCFullYear();
      const s1 = astronomy.Seasons(y);
      const s2 = astronomy.Seasons(y + 1);
      const events = [
        { type: 'March equinox', t: s1.mar_equinox?.date || s1.mar_equinox },
        { type: 'September equinox', t: s1.sep_equinox?.date || s1.sep_equinox },
        { type: 'March equinox', t: s2.mar_equinox?.date || s2.mar_equinox },
        { type: 'September equinox', t: s2.sep_equinox?.date || s2.sep_equinox },
      ].filter(e => e.t instanceof Date);
      events.sort((a,b) => a.t - b.t);
      const next = events.find(e => e.t > date);
      if (!next) return { error: 'No equinox found' };
      return { type: next.type, date: next.t.toISOString(), daysUntil: (next.t - date) / 86400000 };
    } catch (_e) {
      return { error: 'Could not compute equinox' };
    }
  }

  /**
   * Next solstice after given date (June/Dec) using Astronomy.Seasons.
   */
  getNextSolstice(date) {
    try {
      const y = date.getUTCFullYear();
      const s1 = astronomy.Seasons(y);
      const s2 = astronomy.Seasons(y + 1);
      const events = [
        { type: 'June solstice', t: s1.jun_solstice?.date || s1.jun_solstice },
        { type: 'December solstice', t: s1.dec_solstice?.date || s1.dec_solstice },
        { type: 'June solstice', t: s2.jun_solstice?.date || s2.jun_solstice },
        { type: 'December solstice', t: s2.dec_solstice?.date || s2.dec_solstice },
      ].filter(e => e.t instanceof Date);
      events.sort((a,b) => a.t - b.t);
      const next = events.find(e => e.t > date);
      if (!next) return { error: 'No solstice found' };
      return { type: next.type, date: next.t.toISOString(), daysUntil: (next.t - date) / 86400000 };
    } catch (_e) {
      return { error: 'Could not compute solstice' };
    }
  }

  /**
   * Calculate lunar orbital nodes position
   * The nodes precess (move backwards) with an 18.613 year period
   */
  getLunarNodes(date) {
    // Lunar nodes: points where Moon's orbit crosses the ecliptic plane
    // The nodes precess (move backwards) completing a full cycle in 18.613 years
    // This is known as the "Draconic" or "Nodal" cycle

    const NODAL_PERIOD_DAYS = 6798.375; // 18.613 years in days
    const NODAL_PERIOD_YEARS = 18.613;

    // Reference epoch: J2000.0 (January 1, 2000, 12:00 TT)
    // Ascending node was at ~125.04° on this date
    const referenceDate = new Date('2000-01-01T12:00:00Z');
    const REFERENCE_ASCENDING_NODE = 125.04; // degrees

    // Calculate days since reference
    const daysSince = (date - referenceDate) / (1000 * 60 * 60 * 24);

    // Node motion: -19.3416° per year (retrograde/westward)
    const nodeMotionPerDay = -19.3416 / 365.25;

    // Calculate current ascending node position
    let ascendingNode = (REFERENCE_ASCENDING_NODE + (nodeMotionPerDay * daysSince)) % 360;
    if (ascendingNode < 0) ascendingNode += 360;

    // Descending node is 180° opposite
    const descendingNode = (ascendingNode + 180) % 360;

    // Progress through current nodal cycle
    const cycleProgress = (daysSince % NODAL_PERIOD_DAYS) / NODAL_PERIOD_DAYS;

    // Days until next node passage (approximate)
    const moonLongitude = astronomy.EclipticGeoMoon(date).lon;

    // Calculate angular distance to nearest node
    const distToAscending = Math.abs(((moonLongitude - ascendingNode + 180) % 360) - 180);
    const distToDescending = Math.abs(((moonLongitude - descendingNode + 180) % 360) - 180);
    const distToNearestNode = Math.min(distToAscending, distToDescending);

    // Approximate days until node passage (moon moves ~13.2°/day)
    const daysUntilNodePassage = distToNearestNode / 13.2;

    return {
      ascendingNode: ascendingNode, // degrees (where moon crosses ecliptic northward)
      descendingNode: descendingNode, // degrees (where moon crosses ecliptic southward)
      period: {
        days: NODAL_PERIOD_DAYS,
        years: NODAL_PERIOD_YEARS
      },
      progress: cycleProgress, // 0-1 through current 18.6 year cycle
      anglePosition: cycleProgress * 360, // degrees for display dial
      motionRate: nodeMotionPerDay, // degrees per day (negative = retrograde)
      nextNodePassage: {
        daysUntil: daysUntilNodePassage,
        type: distToAscending < distToDescending ? 'ascending' : 'descending'
      }
    };
  }
}

module.exports = AntikytheraEngine;

const express = require('express');
const fetch = require('node-fetch');

const app = express();
const cache = new Map();
const TTL = 10000; // 10 seconds

// In-memory cache with TTL
function getCachedData(key) {
  const cached = cache.get(key);
  if (cached && Date.now() - cached.timestamp < TTL) {
    return cached.data;
  }
  return null;
}

function setCachedData(key, data) {
  cache.set(key, {
    data,
    timestamp: Date.now()
  });
}

// Proxy endpoint
app.get('/api/state', async (req, res) => {
  const cacheKey = `state-${JSON.stringify(req.query)}`;

  // Check cache first
  const cached = getCachedData(cacheKey);
  if (cached) {
    return res.json({ ...cached, cached: true });
  }

  // Cache miss - fetch from Antikythera Engine
  const queryString = new URLSearchParams(req.query).toString();
  const response = await fetch(`http://localhost:3000/api/state?${queryString}`);
  const data = await response.json();

  // Store in cache
  setCachedData(cacheKey, data);

  res.json({ ...data, cached: false });
});

app.listen(4000, () => {
  console.log('Caching proxy running on http://localhost:4000');
});

const fs = require('fs');
const path = require('path');
const { EventEmitter } = require('events');
const chokidar = require('chokidar');
const { validateConfig } = require('../config/schema');

class ConfigLoader extends EventEmitter {
  constructor() {
    super();
    this.config = null;
    this.watchers = [];
    this.looseMode = process.env.ANTIKYTHERA_CONFIG_LOOSE === '1';

    // Load initial config
    this.reload();

    // Setup file watching for hot reload
    this.setupWatchers();
  }

  getConfig() {
    if (!this.config) {
      this.reload();
    }
    return this.config;
  }

  reload() {
    try {
      const merged = this.loadLayered();
      this.config = merged;
      this.emit('reload', this.config);
    } catch (error) {
      console.error('Failed to load configuration:', error.message);
      if (!this.looseMode) {
        throw error;
      }
      // In loose mode, use defaults if reload fails
      if (!this.config) {
        console.warn('Using default configuration due to load failure');
        this.config = this.loadDefault();
      }
    }
  }

  loadLayered() {
    // Layer 3 (base): Default config (always exists)
    const defaultConfig = this.loadDefault();

    // Layer 2: Local config (optional)
    const localPath = path.join(process.cwd(), 'config', 'settings.local.json');
    const localConfig = this.loadFile(localPath, true);

    // Layer 1 (highest): Custom config path (optional)
    const customPath = process.env.ANTIKYTHERA_CONFIG;
    const customConfig = customPath ? this.loadFile(customPath, false) : null;

    // Merge layers: custom > local > default
    let merged = { ...defaultConfig };
    if (localConfig) {
      merged = this.deepMerge(merged, localConfig);
    }
    if (customConfig) {
      merged = this.deepMerge(merged, customConfig);
    }

    // Validate merged config
    const filePath = customPath || (localConfig ? localPath : 'config/settings.default.json');
    const validated = validateConfig(merged, filePath, this.looseMode);

    return validated;
  }

  loadDefault() {
    const defaultPath = path.join(__dirname, '..', 'config', 'settings.default.json');
    return this.loadFile(defaultPath, false);
  }

  loadFile(filePath, optional = false) {
    try {
      if (!fs.existsSync(filePath)) {
        if (optional) {
          return null;
        }
        throw new Error(`Config file not found: ${filePath}`);
      }

      const content = fs.readFileSync(filePath, 'utf8');
      return JSON.parse(content);
    } catch (error) {
      if (optional && error.code === 'ENOENT') {
        return null;
      }
      throw new Error(`Failed to load config from ${filePath}: ${error.message}`);
    }
  }

  deepMerge(target, source) {
    const result = { ...target };

    for (const key in source) {
      if (source[key] instanceof Object && !Array.isArray(source[key])) {
        result[key] = this.deepMerge(target[key] || {}, source[key]);
      } else {
        result[key] = source[key];
      }
    }

    return result;
  }

  setupWatchers() {
    // Watch local config file (hot reload)
    // Watch the config directory so we catch file creation
    const localPath = path.join(process.cwd(), 'config', 'settings.local.json');
    const localWatcher = chokidar.watch(localPath, {
      persistent: true,
      ignoreInitial: true,
      // awaitWriteFinish ensures we don't reload on partial writes
      awaitWriteFinish: {
        stabilityThreshold: 100,
        pollInterval: 25
      }
    });

    localWatcher.on('add', () => {
      console.log('Local config created, reloading...');
      this.reload();
    });

    localWatcher.on('change', () => {
      console.log('Local config changed, reloading...');
      this.reload();
    });

    this.watchers.push(localWatcher);

    // Watch custom config path if specified (hot reload)
    const customPath = process.env.ANTIKYTHERA_CONFIG;
    if (customPath && fs.existsSync(customPath)) {
      const customWatcher = chokidar.watch(customPath, {
        persistent: true,
        ignoreInitial: true
      });

      customWatcher.on('change', () => {
        console.log('Custom config changed, reloading...');
        this.reload();
      });

      this.watchers.push(customWatcher);
    }

    // Note: settings.default.json is NOT watched (requires restart)
  }

  close() {
    // Clean up watchers
    this.watchers.forEach(watcher => watcher.close());
    this.watchers = [];
  }
}

// Singleton instance
let instance = null;

function getConfigLoader() {
  if (!instance) {
    instance = new ConfigLoader();
  }
  return instance;
}

module.exports = {
  ConfigLoader,
  getConfigLoader
};

const fs = require('fs');
const path = require('path');
const { ConfigLoader } = require('./config-loader');

// Mock file system for isolated testing
jest.mock('fs');
jest.mock('chokidar');

describe('ConfigLoader', () => {
  let mockFS;
  let originalCwd;
  let originalEnv;

  beforeEach(() => {
    // Save original environment
    originalCwd = process.cwd();
    originalEnv = { ...process.env };

    // Reset mocks
    jest.clearAllMocks();

    // Setup default mock filesystem
    mockFS = {
      [path.join(originalCwd, 'config', 'settings.default.json')]: JSON.stringify({
        configVersion: 1,
        server: { port: 3000, host: 'localhost' },
        observer: { mode: 'auto', location: { latitude: null, longitude: null, timezone: null, elevation: 0 } },
        api: { rateLimit: { enabled: true, maxRequests: 100, windowMs: 60000 }, cache: { enabled: true, ttlSeconds: 300 } },
        display: { language: 'english', showSunriseSunset: true }
      })
    };

    // Mock fs.existsSync
    fs.existsSync.mockImplementation((filePath) => {
      return mockFS[filePath] !== undefined;
    });

    // Mock fs.readFileSync
    fs.readFileSync.mockImplementation((filePath, encoding) => {
      if (mockFS[filePath] === undefined) {
        const error = new Error(`ENOENT: no such file or directory, open '${filePath}'`);
        error.code = 'ENOENT';
        throw error;
      }
      return mockFS[filePath];
    });

    // Mock fs.mkdirSync (no-op for tests)
    fs.mkdirSync.mockImplementation(() => {});

    // Mock chokidar.watch
    const chokidar = require('chokidar');
    chokidar.watch.mockReturnValue({
      on: jest.fn().mockReturnThis(),
      close: jest.fn()
    });
  });

  afterEach(() => {
    // Restore environment
    process.env = originalEnv;
    jest.restoreAllMocks();
  });

  describe('Default configuration loading', () => {
    test('loads default config successfully', () => {
      const loader = new ConfigLoader();
      const config = loader.getConfig();

      expect(config).toBeDefined();
      expect(config.configVersion).toBe(1);
      expect(config.server.port).toBe(3000);
      expect(config.observer.mode).toBe('auto');
    });

    test('caches config after first load', () => {
      const loader = new ConfigLoader();

      // First call
      const config1 = loader.getConfig();
      // Second call - should use cached config
      const config2 = loader.getConfig();

      expect(config1).toBe(config2); // Same object reference
      expect(fs.readFileSync).toHaveBeenCalledTimes(1); // Only read once
    });
  });

  describe('Configuration layering', () => {
    test('merges local config over default', () => {
      // Add local config to mock filesystem
      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({
        server: { port: 4000 }, // Override port
        observer: { mode: 'manual', location: { latitude: 37.9838, longitude: 23.7275, timezone: 'Europe/Athens', elevation: 0 } }
      });

      const loader = new ConfigLoader();
      const config = loader.getConfig();

      // Local overrides should win
      expect(config.server.port).toBe(4000);
      expect(config.server.host).toBe('localhost'); // From default
      expect(config.observer.mode).toBe('manual');
      expect(config.observer.location.latitude).toBe(37.9838);
    });

    test('merges custom config over local and default', () => {
      // Add local config
      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({
        server: { port: 4000 }
      });

      // Add custom config
      const customPath = '/tmp/custom-config.json';
      mockFS[customPath] = JSON.stringify({
        server: { port: 5000 }, // Overrides local
        display: { language: 'greek' } // Overrides default
      });

      process.env.ANTIKYTHERA_CONFIG = customPath;

      const loader = new ConfigLoader();
      const config = loader.getConfig();

      // Custom config has highest priority
      expect(config.server.port).toBe(5000);
      expect(config.display.language).toBe('greek');
      expect(config.server.host).toBe('localhost'); // From default
    });

    test('deep merges nested objects', () => {
      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({
        observer: {
          location: {
            latitude: 37.9838,
            longitude: 23.7275
            // timezone not specified - should use default (null)
          }
        }
      });

      const loader = new ConfigLoader();
      const config = loader.getConfig();

      // Should merge deeply, not replace entire observer object
      expect(config.observer.mode).toBe('auto'); // From default
      expect(config.observer.location.latitude).toBe(37.9838); // From local
      expect(config.observer.location.timezone).toBeNull(); // From default
    });
  });

  describe('Validation integration', () => {
    test('validates merged config and throws on error', () => {
      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({
        configVersion: 2 // Invalid version
      });

      expect(() => new ConfigLoader()).toThrow(/Configuration Error/);
    });

    test('applies loose mode validation when env var set', () => {
      process.env.ANTIKYTHERA_CONFIG_LOOSE = '1';

      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({
        observer: {
          mode: 'manual',
          location: { latitude: null, longitude: null, timezone: null, elevation: 0 }
        }
      });

      // Should not throw in loose mode
      const loader = new ConfigLoader();
      const config = loader.getConfig();

      expect(config).toBeDefined();
    });
  });

  describe('Hot reload behavior', () => {
    test('sets up watcher for local config if it exists', () => {
      const chokidar = require('chokidar');
      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({ server: { port: 4000 } });

      new ConfigLoader();

      expect(chokidar.watch).toHaveBeenCalledWith(
        localPath,
        expect.objectContaining({ persistent: true, ignoreInitial: true })
      );
    });

    test('watches local config even if it does not exist initially', () => {
      const chokidar = require('chokidar');
      const localPath = path.join(originalCwd, 'config', 'settings.local.json');

      // Local config doesn't exist initially
      delete mockFS[localPath];

      new ConfigLoader();

      // Should still set up watcher to catch file creation
      expect(chokidar.watch).toHaveBeenCalledWith(
        localPath,
        expect.objectContaining({ persistent: true, ignoreInitial: true })
      );
    });

    test('sets up watcher for custom config path if specified', () => {
      const chokidar = require('chokidar');
      const customPath = '/tmp/custom-config.json';
      mockFS[customPath] = JSON.stringify({ server: { port: 5000 } });
      process.env.ANTIKYTHERA_CONFIG = customPath;

      new ConfigLoader();

      expect(chokidar.watch).toHaveBeenCalledWith(
        customPath,
        expect.objectContaining({ persistent: true, ignoreInitial: true })
      );
    });

    test('emits reload event when config changes', (done) => {
      const chokidar = require('chokidar');
      let changeCallback;

      chokidar.watch.mockReturnValue({
        on: jest.fn((event, callback) => {
          if (event === 'change') {
            changeCallback = callback;
          }
          return { on: jest.fn(), close: jest.fn() };
        }),
        close: jest.fn()
      });

      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({ server: { port: 4000 } });

      const loader = new ConfigLoader();

      loader.on('reload', (newConfig) => {
        expect(newConfig).toBeDefined();
        expect(newConfig.server.port).toBe(5000);
        done();
      });

      // Simulate file change
      mockFS[localPath] = JSON.stringify({ server: { port: 5000 } });
      if (changeCallback) {
        changeCallback();
      }
    });
  });

  describe('Error handling', () => {
    test('throws error if default config file is missing', () => {
      // Remove default config from mock filesystem
      const defaultPath = path.join(originalCwd, 'config', 'settings.default.json');
      delete mockFS[defaultPath];

      expect(() => new ConfigLoader()).toThrow(/Config file not found/);
    });

    test('throws error if default config has invalid JSON', () => {
      const defaultPath = path.join(originalCwd, 'config', 'settings.default.json');
      mockFS[defaultPath] = 'invalid json{';

      expect(() => new ConfigLoader()).toThrow();
    });

    test('ignores missing local config file (optional)', () => {
      // Local config doesn't exist - should not throw
      const loader = new ConfigLoader();
      const config = loader.getConfig();

      expect(config).toBeDefined();
      expect(config.server.port).toBe(3000); // Uses default
    });

    test('throws error if custom config specified but not found', () => {
      process.env.ANTIKYTHERA_CONFIG = '/nonexistent/path.json';

      expect(() => new ConfigLoader()).toThrow(/Config file not found/);
    });

    test('uses cached config if reload fails in loose mode', () => {
      process.env.ANTIKYTHERA_CONFIG_LOOSE = '1';

      const loader = new ConfigLoader();
      const initialConfig = loader.getConfig();
      expect(initialConfig.server.port).toBe(3000);

      // Corrupt the default config
      const defaultPath = path.join(originalCwd, 'config', 'settings.default.json');
      mockFS[defaultPath] = 'invalid json';

      // Attempt reload - should use cached config
      loader.reload();
      const configAfterReload = loader.getConfig();

      expect(configAfterReload.server.port).toBe(3000); // Still has cached config
    });
  });

  describe('Resource cleanup', () => {
    test('closes watchers when close() is called', () => {
      const chokidar = require('chokidar');
      const mockWatcher = {
        on: jest.fn().mockReturnThis(),
        close: jest.fn()
      };
      chokidar.watch.mockReturnValue(mockWatcher);

      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({ server: { port: 4000 } });

      const loader = new ConfigLoader();
      loader.close();

      expect(mockWatcher.close).toHaveBeenCalled();
    });
  });

  describe('Environment variable precedence', () => {
    test('ANTIKYTHERA_CONFIG_LOOSE enables loose mode', () => {
      process.env.ANTIKYTHERA_CONFIG_LOOSE = '1';

      const localPath = path.join(originalCwd, 'config', 'settings.local.json');
      mockFS[localPath] = JSON.stringify({
        observer: { mode: 'manual', location: { latitude: null, longitude: null, timezone: null, elevation: 0 } }
      });

      // Should not throw
      const loader = new ConfigLoader();
      expect(loader.looseMode).toBe(true);
    });

    test('ANTIKYTHERA_CONFIG specifies custom config path', () => {
      const customPath = '/custom/path/config.json';
      mockFS[customPath] = JSON.stringify({
        server: { port: 9000 }
      });
      process.env.ANTIKYTHERA_CONFIG = customPath;

      const loader = new ConfigLoader();
      const config = loader.getConfig();

      expect(config.server.port).toBe(9000);
    });
  });
});

// In-memory control state for classroom control mode (process-local)
// All times are treated as UTC ISO strings

const ControlState = {
  active: false,
  mode: null, // 'time' | 'animate' | 'scene'
  displayTime: null, // ISO string
  animate: null, // { from: ISO, to: ISO, speed: number, startedAtMs: number }
  location: null, // { latitude, longitude, elevation?, timezone, name? }
  preset: null,
  bodies: null,
};

const FAR_FUTURE_ISO = '9999-12-31T23:59:59.999Z';
const FAR_FUTURE_MS = new Date(FAR_FUTURE_ISO).getTime();

function reset() {
  ControlState.active = false;
  ControlState.mode = null;
  ControlState.displayTime = null;
  ControlState.animate = null;
  ControlState.location = null;
  ControlState.preset = null;
  ControlState.bodies = null;
}

function setTime(iso) {
  const d = new Date(iso);
  if (isNaN(d.getTime())) throw new Error('Invalid ISO time');
  ControlState.active = true;
  ControlState.mode = 'time';
  ControlState.displayTime = d.toISOString();
  ControlState.animate = null;
}

function setAnimate(fromIso, toIso, speed) {
  const from = new Date(fromIso);
  const to = new Date(toIso);
  if (isNaN(from.getTime()) || isNaN(to.getTime())) throw new Error('Invalid ISO range');
  const span = to.getTime() - from.getTime();
  if (!(span > 0)) throw new Error('Animation range must have to > from');
  const s = Number(speed);
  const spd = Number.isFinite(s) && s > 0 ? s : 1;
  ControlState.active = true;
  ControlState.mode = 'animate';
  ControlState.displayTime = null;
  ControlState.animate = {
    from: from.toISOString(),
    to: to.toISOString(),
    speed: spd,
    startedAtMs: Date.now(),
  };
}

function setScene(preset, bodies) {
  ControlState.active = true;
  ControlState.mode = 'scene';
  ControlState.preset = String(preset || '').trim() || null;
  ControlState.bodies = Array.isArray(bodies) ? bodies : (typeof bodies === 'string' ? bodies.split(',').map(s => s.trim()).filter(Boolean) : null);
}

function run(speed) {
  const s = Number(speed);
  const spd = Number.isFinite(s) && s > 0 ? s : 1;
  const base = effectiveDate(new Date());
  ControlState.active = true;
  ControlState.mode = 'animate';
  ControlState.displayTime = null;
  ControlState.animate = {
    from: new Date(base).toISOString(),
    to: FAR_FUTURE_ISO,
    speed: spd,
    startedAtMs: Date.now(),
  };
}

function pause() {
  // Freeze at current effective time
  const nowEff = effectiveDate(new Date());
  ControlState.active = true;
  ControlState.mode = 'time';
  ControlState.displayTime = new Date(nowEff).toISOString();
  ControlState.animate = null;
}

function stop() {
  reset();
}

function status() {
  return {
    active: ControlState.active,
    mode: ControlState.mode,
    displayTime: ControlState.displayTime,
    animating: Boolean(ControlState.mode === 'animate'),
    animate: ControlState.animate,
    location: ControlState.location,
    preset: ControlState.preset,
    bodies: ControlState.bodies,
  };
}

function effectiveDate(now = new Date()) {
  let result = now;
  if (ControlState.active) {
    if (ControlState.mode === 'time' && ControlState.displayTime) {
      result = new Date(ControlState.displayTime);
    } else if (ControlState.mode === 'animate' && ControlState.animate) {
      const a = ControlState.animate;
      const fromMs = new Date(a.from).getTime();
      const toMs = isFinite(new Date(a.to).getTime()) ? new Date(a.to).getTime() : FAR_FUTURE_MS;
      const elapsed = (Date.now() - a.startedAtMs) * (a.speed || 1);
      const pos = Math.min(toMs, Math.max(fromMs, fromMs + elapsed));
      result = new Date(pos);
    } else {
      // scene mode does not affect time beyond activation
      result = now;
    }
  }
  return result;
}

function setLocation(loc) {
  // Assume validation at API layer
  ControlState.location = loc ? { ...loc } : null;
  ControlState.active = true; // activating control session when location is set
}

function getLocation() {
  return ControlState.location ? { ...ControlState.location } : null;
}

module.exports = {
  ControlState,
  reset,
  setTime,
  setAnimate,
  setScene,
  run,
  pause,
  stop,
  status,
  effectiveDate,
  setLocation,
  getLocation,
};

const fs = require('fs');
const path = require('path');
const crypto = require('crypto');

const TOKEN_FILE = path.join(process.cwd(), '.antikythera', 'control-token');

function getOrCreateControlToken() {
  // Priority 1: Environment variable (for production/override)
  const envToken = process.env.ANTIKYTHERA_CONTROL_TOKEN || process.env.CONTROL_TOKEN;
  if (envToken && String(envToken).trim().length > 0) {
    return String(envToken).trim();
  }

  // Priority 2: Existing local file (REUSE across restarts)
  try {
    if (fs.existsSync(TOKEN_FILE)) {
      const v = fs.readFileSync(TOKEN_FILE, 'utf8').trim();
      if (v) {
        console.log('✓ Using control token from:', TOKEN_FILE);
        return v;
      }
    }
  } catch (err) {
    console.warn('⚠ Could not read token file:', err && err.message ? err.message : String(err));
  }

  // Priority 3: Generate NEW token ONLY if file doesn't exist
  const token = crypto.randomBytes(32).toString('hex');
  try {
    fs.mkdirSync(path.dirname(TOKEN_FILE), { recursive: true });
    fs.writeFileSync(TOKEN_FILE, token, { mode: 0o600 });
    console.log('✓ Generated NEW control token:', TOKEN_FILE);
    console.log('  Token will persist across restarts');
  } catch (_err) {
    console.warn('⚠ Could not save token, using in-memory only');
    console.warn('  Set ANTIKYTHERA_CONTROL_TOKEN env var for persistence');
  }
  return token;
}

module.exports = { getOrCreateControlToken, TOKEN_FILE };

const fetch = require('node-fetch');
const { MS_PER_DAY } = require('../constants/time');
require('dotenv').config({ path: '.env.local' });

// Simple in-memory TTL cache
const cache = new Map(); // key: ip, value: { data, expiresAt }
const TTL_MS = Number(process.env.IP_GEO_TTL_MS || MS_PER_DAY); // default 24h

function now() { return Date.now(); }

function isPrivateIP(ip) {
  if (!ip) return true;
  // Strip IPv6 prefix for IPv4-mapped addresses
  ip = ip.replace('::ffff:', '');
  // Localhost
  if (ip === '127.0.0.1' || ip === '::1') return true;
  // RFC1918 private IPv4 ranges
  const parts = ip.split('.').map(n => parseInt(n, 10));
  if (parts.length === 4) {
    const [a,b] = parts;
    if (a === 10) return true;
    if (a === 172 && b >= 16 && b <= 31) return true;
    if (a === 192 && b === 168) return true;
  }
  // Link-local IPv6
  if (ip.startsWith('fe80:') || ip.startsWith('fc') || ip.startsWith('fd')) return true;
  return false;
}

function parseClientIP(req) {
  // Prefer X-Forwarded-For chain
  const xff = req.headers['x-forwarded-for'];
  if (xff) {
    const list = String(xff).split(',').map(s => s.trim());
    for (const candidate of list) {
      if (!isPrivateIP(candidate)) return candidate;
    }
  }
  // Common reverse proxies
  const cf = req.headers['cf-connecting-ip'];
  if (cf && !isPrivateIP(cf)) return cf;
  const xri = req.headers['x-real-ip'];
  if (xri && !isPrivateIP(xri)) return xri;
  // Fallback to socket address
  const ra = (req.socket && req.socket.remoteAddress) || (req.connection && req.connection.remoteAddress) || req.ip;
  if (ra) return ra;
  return '127.0.0.1';
}

async function fetchIpapi(ip) {
  let url;

  // Hybrid approach: localhost uses auto-detection, production uses client IP
  if (isPrivateIP(ip)) {
    // Dev mode: Let ipapi auto-detect from request origin
    url = 'https://ipapi.co/json/';
  } else {
    // Production: Use client's IP
    url = `https://ipapi.co/${ip}/json/`;
  }

  const res = await fetch(url, { 
    timeout: 5000,
    headers: {
      'User-Agent': 'Antikythera-Engine/1.0'
    }
  });
  if (!res.ok) throw new Error(`ipapi HTTP ${res.status}`);
  return res.json();
}

async function getLocationFromIP(ip) {
  const key = ip || 'unknown';
  const hit = cache.get(key);
  if (hit && hit.expiresAt > now()) {
    // Cache hit - return silently without logging
    return hit.data;
  }

  try {
    const data = await fetchIpapi(ip);

    // Check for ipapi error response
    if (data.error) {
      throw new Error(data.reason || 'ipapi error');
    }

    const out = {
      latitude: data.latitude,
      longitude: data.longitude,
      city: data.city,
      state: data.region || '', // Now includes state/region!
      country: data.country_name,
      timezone: data.timezone,
      postal: data.postal || '',
      source: 'ip_geolocation'
    };

    console.log(`Location detected: ${out.city}, ${out.state}, ${out.country}`);
    cache.set(key, { data: out, expiresAt: now() + TTL_MS });
    return out;
  } catch (err) {
    console.error('ipapi.co detection failed:', err.message);

    // Memphis fallback (changed from Athens)
    const out = {
      latitude: 35.1184,
      longitude: -90.0489,
      city: 'Memphis',
      state: 'Tennessee',
      country: 'USA',
      timezone: 'America/Chicago',
      postal: '38103',
      source: 'fallback'
    };

    console.log('Using Memphis fallback location');
    cache.set(key, { data: out, expiresAt: now() + TTL_MS });
    return out;
  }
}

/**
 * Extract observer location from configuration.
 * Returns observer data if mode is 'manual' and required fields are present.
 * 
 * @param {object} config - Configuration object from config loader
 * @returns {object|null} Observer data or null if not in manual mode
 */
function getObserverFromConfig(config) {
  if (!config || !config.observer) {
    return null;
  }

  // Only use config location when mode is 'manual'
  if (config.observer.mode !== 'manual') {
    return null;
  }

  const loc = config.observer.location;

  // Validate required fields for manual mode
  if (!Number.isFinite(loc.latitude) || !Number.isFinite(loc.longitude) || !loc.timezone) {
    return null;
  }

  return {
    latitude: loc.latitude,
    longitude: loc.longitude,
    elevation: loc.elevation || 0,
    timezone: loc.timezone,
    name: loc.name || null,
    source: 'config'
  };
}

/**
 * Resolve observer location from available sources with priority ordering.
 * 
 * Priority order:
 * 1. Query parameters (?lat=X&lon=Y&elev=Z) - temporary override
 * 2. Config observer (when mode === 'manual') - persistent configuration
 * 3. IP geolocation (when mode === 'auto' or no config) - automatic detection
 * 4. Fallback (Memphis, Tennessee) - last resort
 * 
 * Note: Control mode location takes precedence over all sources,
 * but is handled by the caller (server.js) before invoking this function.
 * 
 * @param {object} req - Express request object
 * @param {object} config - Configuration object from config loader
 * @returns {Promise<object>} Observer location data
 */
async function getObserverFromRequest(req, config = null) {
  // Priority 1: Query parameter override (temporary, per-request)
  const lat = req.query.lat;
  const lon = req.query.lon;
  const elev = req.query.elev;

  if (lat && lon && isFinite(lat) && isFinite(lon)) {
    return {
      latitude: parseFloat(lat),
      longitude: parseFloat(lon),
      elevation: elev ? parseFloat(elev) : 0,
      source: 'query'
    };
  }

  // Priority 2: Config observer (manual mode, persistent)
  if (config) {
    const configObserver = getObserverFromConfig(config);
    if (configObserver) {
      return configObserver;
    }
  }

  // Priority 3: IP geolocation (auto mode or no config)
  const ip = parseClientIP(req);
  const loc = await getLocationFromIP(ip);

  return {
    latitude: Number(loc.latitude),
    longitude: Number(loc.longitude),
    elevation: 0,
    city: loc.city,
    state: loc.state || '',
    country: loc.country,
    timezone: loc.timezone,
    postal: loc.postal || '',
    source: loc.source || 'ip_geolocation'
  };
}

module.exports = {
  getLocationFromIP,
  getObserverFromRequest,
  getObserverFromConfig,
  parseClientIP,
  isPrivateIP,
};

class BackLowerFace {
  constructor(canvas) {
    this.canvas = canvas;
    this.ctx = canvas.getContext('2d');

    // Calculate scale factor (1800/600 = 3x for high-res displays)
    this.scale = canvas.width / 600;

    // Use original 600x600 coordinate system, not scaled canvas size
    this.centerX = 300;
    this.centerY = 300;
    this.maxRadius = Math.min(600, 600) / 2 - 60;
    this.spiralTurns = 4.5; // 223 months in ~4.5 turns
  }

  // Helper to replace placeholders in translated strings
  format(template, values) {
    return template.replace(/{(\w+)}/g, (match, key) => {
      return values[key] !== undefined ? values[key] : match;
    });
  }

  render(data) {
    const rotate = (data && data.settings && data.settings.rotate) || (window.appSettings && window.appSettings.rotate) || 'none';
    const angle = rotate === 'ccw90' ? -Math.PI / 2 : (rotate === 'cw90' ? Math.PI / 2 : 0);

    this.ctx.save();
    this.ctx.setTransform(1, 0, 0, 1, 0, 0);
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

    // Apply scale for high-resolution rendering
    this.ctx.scale(this.scale, this.scale);

    if (angle !== 0) {
      this.ctx.translate(300, 300);
      this.ctx.rotate(angle);
      this.ctx.translate(-300, -300);
    }

    this.drawTitle();
    this.drawSpiral();
    this.drawMonthMarkers();
    this.drawEclipseGlyphs();
    this.drawCurrentPosition(data);
    this.drawSubDials(data);
    this.drawInfo(data);

    this.ctx.restore();
  }

  drawTitle() {
    this.ctx.fillStyle = '#d4af37';
    this.ctx.font = 'bold 18px Georgia';
    this.ctx.textAlign = 'center';
    this.ctx.fillText(languageManager.t('back_lower_face.title'), this.centerX, 30);
    //this.ctx.font = '14px Georgia';
    //this.ctx.fillText(languageManager.t('back_lower_face.title'), this.centerX, 50);
  }

  drawSpiral() {
    // Main Archimedean spiral (223 synodic months)
    this.ctx.strokeStyle = '#d4af37';
    this.ctx.lineWidth = 10;
    this.ctx.beginPath();

    for (let i = 0; i <= 1000; i++) {
      const t = i / 1000;
      const angle = t * this.spiralTurns * Math.PI * 2;
      const r = t * this.maxRadius;
      const x = this.centerX + Math.cos(angle) * r;
      const y = this.centerY + Math.sin(angle) * r;

      if (i === 0) {
        this.ctx.moveTo(x, y);
      } else {
        this.ctx.lineTo(x, y);
      }
    }
    this.ctx.stroke();

    // Inner spiral decoration
    this.ctx.strokeStyle = 'rgba(212, 175, 55, 0.3)';
    this.ctx.lineWidth = 1;
    this.ctx.beginPath();

    for (let i = 0; i <= 1000; i++) {
      const t = i / 1000;
      const angle = t * this.spiralTurns * Math.PI * 2;
      const r = t * this.maxRadius - 10;
      const x = this.centerX + Math.cos(angle) * r;
      const y = this.centerY + Math.sin(angle) * r;

      if (i === 0) {
        this.ctx.moveTo(x, y);
      } else {
        this.ctx.lineTo(x, y);
      }
    }
    this.ctx.stroke();
  }

  drawMonthMarkers() {
    // Mark every 10 months along the spiral
    this.ctx.fillStyle = '#d4af37';

    for (let month = 0; month <= 223; month += 10) {
      const t = month / 223;
      const angle = t * this.spiralTurns * Math.PI * 2;
      const r = t * this.maxRadius;
      const x = this.centerX + Math.cos(angle) * r;
      const y = this.centerY + Math.sin(angle) * r;

      this.ctx.beginPath();
      this.ctx.arc(x, y, 3, 0, Math.PI * 2);
      this.ctx.fill();
    }
  }

  drawEclipseGlyphs() {
    // Draw eclipse symbols at key positions
    // Solar eclipses = ☉, Lunar eclipses = ☽
    this.ctx.fillStyle = '#f0e6d2';
    this.ctx.font = '16px Arial';
    this.ctx.textAlign = 'center';
    this.ctx.textBaseline = 'middle';

    // Sample eclipse positions (simplified)
    const eclipsePositions = [0, 30, 60, 90, 120, 150, 180, 210]; // months

    eclipsePositions.forEach((month, idx) => {
      const t = month / 223;
      const angle = t * this.spiralTurns * Math.PI * 2;
      const r = t * this.maxRadius;

      // Offset perpendicular to spiral
      const offsetAngle = angle + Math.PI / 2;
      const x = this.centerX + Math.cos(angle) * r + Math.cos(offsetAngle) * 15;
      const y = this.centerY + Math.sin(angle) * r + Math.sin(offsetAngle) * 15;

      // Alternate solar and lunar
      const symbol = idx % 2 === 0 ? '☉' : '☽';
      this.ctx.fillText(symbol, x, y);
    });
  }

  drawCurrentPosition(data) {
    if (!data.sarosCycle) return;

    const progress = data.sarosCycle.progress;
    const angle = progress * this.spiralTurns * Math.PI * 2;
    const r = progress * this.maxRadius;
    const x = this.centerX + Math.cos(angle) * r;
    const y = this.centerY + Math.sin(angle) * r;

    // Pointer from center
    this.ctx.strokeStyle = '#ffaa00';
    this.ctx.lineWidth = 3;
    this.ctx.beginPath();
    this.ctx.moveTo(this.centerX, this.centerY);
    this.ctx.lineTo(x, y);
    this.ctx.stroke();

    // Current position marker
    this.ctx.fillStyle = '#ff6b35';
    this.ctx.beginPath();
    this.ctx.arc(x, y, 10, 0, Math.PI * 2);
    this.ctx.fill();

    // Outer ring
    this.ctx.strokeStyle = '#ffffff';
    this.ctx.lineWidth = 2;
    this.ctx.stroke();

    // Inner dot
    this.ctx.fillStyle = '#ffffff';
    this.ctx.beginPath();
    this.ctx.arc(x, y, 4, 0, Math.PI * 2);
    this.ctx.fill();
  }

  drawSubDials(data) {
    // Exeligmos sub-dial (triple Saros = 54 years)
    const subDialX = this.canvas.width - 80;
    const subDialY = 100;
    const subDialRadius = 50;

    this.ctx.strokeStyle = '#d4af37';
    this.ctx.lineWidth = 2;
    this.ctx.beginPath();
    this.ctx.arc(subDialX, subDialY, subDialRadius, 0, Math.PI * 2);
    this.ctx.stroke();

    // Three divisions for triple Saros
    for (let i = 0; i < 3; i++) {
      const angle = (i * 120 - 90) * Math.PI / 180;
      this.ctx.strokeStyle = 'var(--color-accent, #d4af37)';
      this.ctx.lineWidth = 1;
      this.ctx.beginPath();
      this.ctx.moveTo(subDialX, subDialY);
      this.ctx.lineTo(
        subDialX + Math.cos(angle) * subDialRadius,
        subDialY + Math.sin(angle) * subDialRadius
      );
      this.ctx.stroke();
    }

    // Exeligmos position (3 Saros cycles)
    if (data.sarosCycle) {
      const exeligmosProgress = (data.sarosCycle.cycle % 3) / 3;
      const angle = exeligmosProgress * Math.PI * 2 - Math.PI / 2;

      this.ctx.strokeStyle = 'var(--color-pointer, #ffaa00)';
      this.ctx.lineWidth = 2;
      this.ctx.beginPath();
      this.ctx.moveTo(subDialX, subDialY);
      this.ctx.lineTo(
        subDialX + Math.cos(angle) * (subDialRadius - 10),
        subDialY + Math.sin(angle) * (subDialRadius - 10)
      );
      this.ctx.stroke();

      // Marker
      this.ctx.fillStyle = '#ff6b35';
      this.ctx.beginPath();
      this.ctx.arc(
        subDialX + Math.cos(angle) * (subDialRadius - 10),
        subDialY + Math.sin(angle) * (subDialRadius - 10),
        5,
        0,
        Math.PI * 2
      );
      this.ctx.fill();
    }

    // Label
    this.ctx.fillStyle = 'var(--color-text, #f0e6d2)';
    this.ctx.font = '11px Georgia';
    this.ctx.textAlign = 'center';
    this.ctx.fillText(languageManager.t('back_lower_face.exeligmos'), subDialX, subDialY + subDialRadius + 15);
    this.ctx.fillText(languageManager.t('back_lower_face.years_54'), subDialX, subDialY + subDialRadius + 28);
  }

  drawInfo(data) {
    if (!data.sarosCycle || !data.nextEclipse) return;

    const saros = data.sarosCycle;
    const nextEclipse = data.nextEclipse;

    // Calculate months into current Saros cycle (0-223)
    const monthsInCycle = Math.round(saros.progress * 223);
    const monthsRemaining = 223 - monthsInCycle;

    // Calculate years and days from cycle completion
    const daysPerSaros = 223 * 29.53059; // 223 synodic months
    const daysRemaining = (monthsRemaining / 223) * daysPerSaros;
    const yearsRemaining = Math.floor(daysRemaining / 365.25);
    const daysInYearRemaining = Math.round(daysRemaining % 365.25);

    // Info panel at bottom
    this.ctx.fillStyle = 'var(--color-accent, #d4af37)';
    this.ctx.font = 'bold 16px Georgia';
    this.ctx.textAlign = 'center';
    this.ctx.fillText(
      this.format(languageManager.t('back_lower_face.saros_month'), {
        cycle: saros.cycle,
        month: monthsInCycle
      }),
      this.centerX,
      this.canvas.height - 65
    );

    this.ctx.font = '12px Georgia';
    this.ctx.fillStyle = 'var(--color-text, #f0e6d2)';
    this.ctx.fillText(
      this.format(languageManager.t('back_lower_face.cycle_ends'), {
        years: yearsRemaining,
        days: daysInYearRemaining
      }),
      this.centerX,
      this.canvas.height - 45
    );

    // Next eclipse info
    this.ctx.font = 'bold 14px Georgia';
    this.ctx.fillStyle = 'var(--color-pointer, #ffaa00)';

    const daysUntil = Math.floor(nextEclipse.daysUntil || 0);
    const eclipseKey = nextEclipse.type === 'lunar' ? 'next_eclipse_lunar' : 'next_eclipse_solar';

    this.ctx.fillText(
      this.format(languageManager.t(`back_lower_face.${eclipseKey}`), {
        days: daysUntil
      }),
      this.centerX,
      this.canvas.height - 25
    );

    // Progress percentage
    this.ctx.font = '11px Georgia';
    this.ctx.fillStyle = 'rgba(212, 175, 55, 0.8)';
    const progressPercent = (saros.progress * 100).toFixed(1);
    this.ctx.fillText(
      this.format(languageManager.t('back_lower_face.progress'), {
        percent: progressPercent
      }),
      this.centerX,
      this.canvas.height - 8
    );
  }
}

