#!/usr/bin/env node

/**
 * Comprehensive HORIZONS Validation
 * Validates ALL celestial bodies against NASA JPL HORIZONS
 */

const fetch = require('node-fetch');
const { MS_PER_MINUTE } = require('../constants/time');

// HORIZONS body codes
const BODY_CODES = {
  sun: '10',
  moon: '301',
  mercury: '199',
  venus: '299',
  mars: '499',
  jupiter: '599',
  saturn: '699'
};

async function validateAllBodies() {
  try {
    // Get API data
    console.log('Fetching Antikythera API data...\n');
    const apiResponse = await fetch('http://localhost:3000/api/display');
    if (!apiResponse.ok) {
      throw new Error(`API returned ${apiResponse.status}`);
    }
    const apiData = await apiResponse.json();
    const timestamp = new Date(apiData.timestamp);
    const coords = apiData.system.debug.ecliptic_coordinates;
    const observer = apiData.system.observer || { latitude: 37.5, longitude: 23.0, elevation: 0, country: 'N/A', source: 'default' };

    console.log(`Timestamp: ${timestamp.toISOString()}`);
    console.log(`Location: ${observer.latitude}°N, ${observer.longitude}°E (${observer.country || 'N/A'})`);
    console.log(`Source: ${observer.source || 'unknown'}\n`);
    console.log('='.repeat(80));

    // Validate each body
    const results = [];
    for (const [bodyName, bodyCode] of Object.entries(BODY_CODES)) {
      const result = await validateBody(bodyName, bodyCode, coords[bodyName], timestamp, observer);
      results.push(result);
    }

    // Summary
    console.log('\n' + '='.repeat(80));
    console.log('VALIDATION SUMMARY');
    console.log('='.repeat(80));

    const passed = results.filter(r => r.passed).length;
    const total = results.length;

    results.forEach(r => {
      const status = r.passed ? '[PASS]' : '[FAIL]';
      const lonStr = `${r.delta_lon.toFixed(4)}°`.padStart(10);
      const latStr = `${r.delta_lat.toFixed(4)}°`.padStart(10);
      console.log(`${status} ${r.body.padEnd(10)} Δlon=${lonStr}  Δlat=${latStr}`);
    });

    console.log(`\nResult: ${passed}/${total} passed`);

    if (passed === total) {
      console.log('\n✓ All bodies within display quality precision');
      process.exit(0);
    } else {
      console.log('\n✗ Some bodies exceed tolerance');
      process.exit(1);
    }

  } catch (error) {
    console.error('\n[ERROR]', error.message);
    process.exit(1);
  }
}

async function validateBody(bodyName, bodyCode, apiCoords, timestamp, observer) {
  console.log(`\n${bodyName.toUpperCase()}`);
  console.log('-'.repeat(40));

  try {
    // Query HORIZONS
    const horizons = await queryHORIZONS(bodyCode, timestamp, observer);

    if (!horizons) {
      console.log(`  API:      lon=${apiCoords.lon.toFixed(4)}°  lat=${apiCoords.lat.toFixed(4)}°`);
      console.log('  HORIZONS: [QUERY FAILED]');
      return {
        body: bodyName,
        passed: false,
        delta_lon: 999,
        delta_lat: 999
      };
    }

    // Calculate deltas
    const delta_lon = Math.abs(horizons.lon - apiCoords.lon);
    const delta_lat = Math.abs(horizons.lat - apiCoords.lat);

    console.log(`  API:      lon=${apiCoords.lon.toFixed(6)}°  lat=${apiCoords.lat.toFixed(6)}°`);
    console.log(`  HORIZONS: lon=${horizons.lon.toFixed(6)}°  lat=${horizons.lat.toFixed(6)}°`);
    console.log(`  Delta:    lon=${delta_lon.toFixed(6)}°  lat=${delta_lat.toFixed(6)}°`);
    console.log(`            (${(delta_lon * 3600).toFixed(1)}" lon, ${(delta_lat * 3600).toFixed(1)}" lat)`);

    const TOLERANCE_DEG = 0.1;
    const passed = delta_lon < TOLERANCE_DEG && delta_lat < TOLERANCE_DEG;

    console.log(`  ${passed ? '[PASS]' : '[FAIL]'} ${passed ? 'Within' : 'Exceeds'} tolerance`);

    return {
      body: bodyName,
      passed,
      delta_lon,
      delta_lat
    };

  } catch (error) {
    console.log(`  [ERROR] ${error.message}`);
    return {
      body: bodyName,
      passed: false,
      delta_lon: 999,
      delta_lat: 999
    };
  }
}

async function queryHORIZONS(bodyCode, date, observer) {
  const stop = new Date(date.getTime() + MS_PER_MINUTE);

  const params = new URLSearchParams({
    format: 'text',
    COMMAND: `'${bodyCode}'`,
    MAKE_EPHEM: 'YES',
    EPHEM_TYPE: 'OBSERVER',
    CENTER: "'coord@399'",
    COORD_TYPE: 'GEODETIC',
    SITE_COORD: `'${observer.longitude},${observer.latitude},${observer.elevation || 0}'`,
    START_TIME: `'${formatHORIZONSTime(date)}'`,
    STOP_TIME: `'${formatHORIZONSTime(stop)}'`,
    STEP_SIZE: "'1 m'",
    QUANTITIES: "'31'",
    ANG_FORMAT: 'DEG',
    TIME_TYPE: 'UT',
    TIME_DIGITS: 'SECONDS',
    REF_PLANE: 'ECLIPTIC',
    REF_SYSTEM: 'J2000',
    CSV_FORMAT: 'YES'
  });

  try {
    const response = await fetch(`https://ssd.jpl.nasa.gov/api/horizons.api?${params}`);
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }

    const text = await response.text();
    // Log reference frame lines once for debugging
    if (!global.__printedHorizonsHeader) {
      const headerLines = text.split('\n').slice(0, 60).filter(l => /Reference|Ecliptic|Equinox|Frame/i.test(l));
      if (headerLines.length) {
        console.log('    HORIZONS Header (frame):');
        headerLines.forEach(l => console.log('    ', l));
        global.__printedHorizonsHeader = true;
      }
    }
    return parseHORIZONSCSV(text);

  } catch (error) {
    console.log(`    HORIZONS query error: ${error.message}`);
    return null;
  }
}

function formatHORIZONSTime(date) {
  // Preserve seconds to avoid lunar fast motion discrepancies
  return date.toISOString().replace('T', ' ').split('.')[0];
}

function parseHORIZONSCSV(text) {
  const lines = text.split('\n');

  const soeIndex = lines.findIndex(l => l.includes('$$SOE'));
  const eoeIndex = lines.findIndex(l => l.includes('$$EOE'));

  if (soeIndex === -1 || eoeIndex === -1) return null;

  const headerIndex = soeIndex - 2;
  if (headerIndex < 0) return null;

  const header = parseCSVLine(lines[headerIndex]);
  const dataRow = parseCSVLine(lines[soeIndex + 1]);

  const lonIdx = header.findIndex(h => /ObsEcLon/i.test(h));
  const latIdx = header.findIndex(h => /ObsEcLat/i.test(h));

  if (lonIdx === -1 || latIdx === -1) return null;

  return {
    lon: parseFloat(dataRow[lonIdx]),
    lat: parseFloat(dataRow[latIdx])
  };
}

function parseCSVLine(line) {
  return line.split(',').map(s => s.replace(/(^"|"$)/g, '').trim());
}

if (require.main === module) {
  validateAllBodies();
}

module.exports = { validateAllBodies, validateBody };

#!/usr/bin/env node

/**
 * Extended validation for VSOP87 (astronomy-engine) against NASA JPL HORIZONS
 * - Samples multiple timestamps
 * - Compares lon/lat per body
 * - Reports p50 / p95 / max (arcseconds)
 */

const fetch = require('node-fetch');
const { MS_PER_MINUTE, MS_PER_DAY } = require('../constants/time');

const BODY_CODES = {
  sun: '10',
  moon: '301',
  mercury: '199',
  venus: '299',
  mars: '499',
  jupiter: '599',
  saturn: '699',
};

function parseArgs() {
  const args = process.argv.slice(2);
  const opts = { samples: 48, spanDays: 30, start: null };
  for (let i = 0; i < args.length; i++) {
    const a = args[i];
    if (a === '--samples' && args[i+1]) { opts.samples = parseInt(args[++i], 10); }
    else if (a === '--span-days' && args[i+1]) { opts.spanDays = parseFloat(args[++i]); }
    else if (a === '--start' && args[i+1]) { opts.start = new Date(args[++i]); }
  }
  if (!opts.start || isNaN(opts.start)) opts.start = new Date();
  return opts;
}

function wrapLonDiffDeg(a, b) {
  // Smallest absolute difference in degrees
  const d = (a - b + 540) % 360 - 180;
  return Math.abs(d);
}

function quantile(sorted, p) {
  if (sorted.length === 0) return null;
  const idx = (sorted.length - 1) * p;
  const lo = Math.floor(idx);
  const hi = Math.ceil(idx);
  if (lo === hi) return sorted[lo];
  const h = idx - lo;
  return sorted[lo] * (1 - h) + sorted[hi] * h;
}

async function queryHorizons(bodyCode, date, observer) {
  const stop = new Date(date.getTime() + MS_PER_MINUTE);
  const params = new URLSearchParams({
    format: 'text',
    COMMAND: `'${bodyCode}'`,
    MAKE_EPHEM: 'YES',
    EPHEM_TYPE: 'OBSERVER',
    CENTER: "'coord@399'",
    COORD_TYPE: 'GEODETIC',
    SITE_COORD: `'${observer.longitude},${observer.latitude},${observer.elevation || 0}'`,
    START_TIME: `'${date.toISOString().replace('T',' ').split('.')[0]}'`,
    STOP_TIME: `'${stop.toISOString().replace('T',' ').split('.')[0]}'`,
    STEP_SIZE: "'1 m'",
    QUANTITIES: "'31'",
    ANG_FORMAT: 'DEG',
    TIME_TYPE: 'UT',
    TIME_DIGITS: 'SECONDS',
    REF_PLANE: 'ECLIPTIC',
    REF_SYSTEM: 'J2000',
    CSV_FORMAT: 'YES'
  });
  const res = await fetch(`https://ssd.jpl.nasa.gov/api/horizons.api?${params}`);
  if (!res.ok) throw new Error(`HORIZONS HTTP ${res.status}`);
  const text = await res.text();
  const lines = text.split('\n');
  const soe = lines.findIndex(l => l.includes('$$SOE'));
  const eoe = lines.findIndex(l => l.includes('$$EOE'));
  if (soe < 0 || eoe < 0) throw new Error('Parse error');
  const header = lines[soe - 2].split(',').map(s => s.replace(/(^"|"$)/g,'').trim());
  const row = lines[soe + 1].split(',').map(s => s.replace(/(^"|"$)/g,'').trim());
  const lonIdx = header.findIndex(h => /ObsEcLon/i.test(h));
  const latIdx = header.findIndex(h => /ObsEcLat/i.test(h));
  if (lonIdx < 0 || latIdx < 0) throw new Error('No ObsEcLon/ObsEcLat');
  return { lon: parseFloat(row[lonIdx]), lat: parseFloat(row[latIdx]) };
}

async function getAPI(dateISO, observer) {
  const q = new URLSearchParams({ date: dateISO, lat: String(observer.latitude), lon: String(observer.longitude), elev: String(observer.elevation || 0), precision: 'full' });
  const res = await fetch(`http://localhost:3000/api/display?${q}`);
  if (!res.ok) throw new Error(`/api/display HTTP ${res.status}`);
  const data = await res.json();
  return data.system.debug.ecliptic_coordinates; // { sun:{lon,lat}, ... }
}

async function main() {
  const { samples, spanDays, start } = parseArgs();
  const observer = { latitude: 37.5, longitude: 23.0, elevation: 0 };

  const perBody = {};
  Object.keys(BODY_CODES).forEach(b => {
    perBody[b] = { lon: [], lat: [] };
  });

  console.log(`Sampling ${samples} timestamps across ${spanDays} day(s) starting ${start.toISOString()}`);
  console.log(`Observer: ${observer.latitude}°N, ${observer.longitude}°E\n`);

  for (let i = 0; i < samples; i++) {
    const t = new Date(start.getTime() + (i * spanDays * MS_PER_DAY) / Math.max(1, samples-1));
    const dateISO = t.toISOString();

    process.stdout.write(`[${i+1}/${samples}] ${dateISO.slice(0,19)}Z `);

    try {
      const positions = await getAPI(dateISO, observer);

      for (const [body, code] of Object.entries(BODY_CODES)) {
        try {
          const h = await queryHorizons(code, t, observer);
          const mine = positions[body];
          if (!mine) throw new Error('No API data');
          const dlon = wrapLonDiffDeg(h.lon, mine.lon) * 3600;
          const dlat = Math.abs(h.lat - mine.lat) * 3600;
          perBody[body].lon.push(dlon);
          perBody[body].lat.push(dlat);
        } catch (_err) {
          // Skip sample on error for this body
        }
      }
      console.log('✓');
    } catch (err) {
      console.log(`✗ ${err.message}`);
    }
  }

  // Summarize
  const summary = {};
  for (const body of Object.keys(BODY_CODES)) {
    const L = perBody[body].lon.slice().sort((a,b)=>a-b);
    const B = perBody[body].lat.slice().sort((a,b)=>a-b);
    summary[body] = {
      lon: {
        p50: quantile(L, 0.5),
        p95: quantile(L, 0.95),
        max: L.length ? L[L.length-1] : null,
        n: L.length,
      },
      lat: {
        p50: quantile(B, 0.5),
        p95: quantile(B, 0.95),
        max: B.length ? B[B.length-1] : null,
        n: B.length,
      }
    };
  }

  // Print concise table
  console.log('\n' + '='.repeat(80));
  console.log('VSOP87 (astronomy-engine) vs HORIZONS (arcsec)');
  console.log('='.repeat(80));
  for (const [body, stats] of Object.entries(summary)) {
    console.log(`${body.padEnd(8)} lon p50=${stats.lon.p50?.toFixed(2).padStart(5)}  p95=${stats.lon.p95?.toFixed(2).padStart(5)}  max=${stats.lon.max?.toFixed(2).padStart(5)} | lat p50=${stats.lat.p50?.toFixed(2).padStart(5)}  p95=${stats.lat.p95?.toFixed(2).padStart(5)}  max=${stats.lat.max?.toFixed(2).padStart(5)}  (n=${stats.lon.n})`);
  }

  // Global stats
  const allLon = Object.values(perBody).flatMap(b => b.lon).sort((a,b)=>a-b);
  const allLat = Object.values(perBody).flatMap(b => b.lat).sort((a,b)=>a-b);
  console.log('='.repeat(80));
  console.log(`OVERALL  lon p50=${quantile(allLon, 0.5)?.toFixed(2).padStart(5)}  p95=${quantile(allLon, 0.95)?.toFixed(2).padStart(5)}  max=${allLon.length ? allLon[allLon.length-1].toFixed(2) : 'N/A'} | lat p50=${quantile(allLat, 0.5)?.toFixed(2).padStart(5)}  p95=${quantile(allLat, 0.95)?.toFixed(2).padStart(5)}  max=${allLat.length ? allLat[allLat.length-1].toFixed(2) : 'N/A'}`);
  console.log('='.repeat(80) + '\n');

  // Emit JSON blob
  const out = { 
    engine: 'VSOP87 via astronomy-engine',
    span: { start: start.toISOString(), end: new Date(start.getTime()+spanDays*MS_PER_DAY).toISOString() },
    samples, observer, summary,
    aggregate: {
      lon: { p50: quantile(allLon, 0.5), p95: quantile(allLon, 0.95), max: allLon.length ? allLon[allLon.length-1] : null },
      lat: { p50: quantile(allLat, 0.5), p95: quantile(allLat, 0.95), max: allLat.length ? allLat[allLat.length-1] : null }
    }
  };
  console.log('JSON:');
  console.log(JSON.stringify(out, null, 2));
}

if (require.main === module) {
